    String palabra="";
    
    private void IniciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_IniciarActionPerformed
        palabra = palabrasecreto.getText();
        palabrasecreto.setText("");
        int longi = palabra.length();
        String placeholder = "";
        for (int i = 0; i < longi; i++) {
            placeholder = placeholder + "/";
        }
        resul.setText(placeholder);
        fail.setText("0");
        letra.setText("");
        letrasfail.setText("");
        Iniciar.setEnabled(false);
        ingresar.setEnabled(true);
    }//GEN-LAST:event_IniciarActionPerformed

    private void ingresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ingresarActionPerformed
        String letr = letra.getText();
        char let = letr.charAt(0);
        int longi = palabra.length();
        int error = Integer.valueOf(fail.getText());
        String placeholder = resul.getText();
        String letrasmalas = letrasfail.getText();
        boolean estado = false;
        char[] placeholderArray = placeholder.toCharArray();

        if (error < 4) {
            for (int i = 0; i < longi; i++) {
                if (let == palabra.charAt(i)) {
                    if (placeholderArray[i] == '/') {
                        placeholderArray[i] = let;
                        estado = true;
                    }
                }
            }

            if (!estado) {
                error++;
                letrasmalas = letrasmalas + let + "-";
            }

            placeholder = String.valueOf(placeholderArray);
        } else {
            titulo.setText("GAME OVER");
            ingresar.setEnabled(false);
            Iniciar.setEnabled(true);
            error++;
            letrasmalas = letrasmalas + let;
            placeholder = palabra;
        }

        resul.setText(placeholder);
        fail.setText(String.valueOf(error));
        letrasfail.setText(letrasmalas);

        if (!placeholder.contains("/")) {
            if (error < 4) {
                titulo.setText("Â¡HAS GANADO!");
            } else {
                titulo.setText("GAME OVER");
            }
            ingresar.setEnabled(false);
            Iniciar.setEnabled(true);
        }
    }//GEN-LAST:event_ingresarActionPerformed

